import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    host: "0.0.0.0",
    port: 5000,
    strictPort: true,
    allowedHosts: "all",
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
server.listen({
  port,
  host: "0.0.0.0",   // Allow all hosts for Replit proxy
}, () => {
  log(`serving on http://0.0.0.0:${port}`);
});

})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { weatherService } from "./services/weather";
import { geminiService } from "./services/gemini";
import { 
  insertWeatherDataSchema, 
  insertCatchLogSchema, 
  insertChatMessageSchema 
} from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Weather endpoints
  app.get("/api/weather/current", async (req, res) => {
    try {
      const { lat, lon } = req.query;

      if (!lat || !lon) {
        return res.status(400).json({ error: "Latitude and longitude are required" });
      }

      const weatherData = await weatherService.getCurrentWeather(
        parseFloat(lat as string), 
        parseFloat(lon as string)
      );

      // Save to storage
      const savedWeather = await storage.saveWeatherData(weatherData);

      // Get fishing conditions
      const fishingConditions = weatherService.getFishingConditions(weatherData);

      res.json({
        weather: savedWeather,
        fishingConditions
      });
    } catch (error) {
      console.error('Weather API error:', error);
      res.status(500).json({ error: "Failed to fetch weather data" });
    }
  });

  app.get("/api/weather/forecast", async (req, res) => {
    try {
      const { lat, lon } = req.query;

      if (!lat || !lon) {
        return res.status(400).json({ error: "Latitude and longitude are required" });
      }

      const forecast = await weatherService.getWeatherForecast(
        parseFloat(lat as string), 
        parseFloat(lon as string)
      );

      res.json(forecast);
    } catch (error) {
      console.error('Weather forecast error:', error);
      res.status(500).json({ error: "Failed to fetch weather forecast" });
    }
  });

  // Fishing zones endpoints
  app.get("/api/fishing-zones", async (req, res) => {
    try {
      const zones = await storage.getFishingZones();
      res.json(zones);
    } catch (error) {
      console.error('Fishing zones error:', error);
      res.status(500).json({ error: "Failed to fetch fishing zones" });
    }
  });

  app.get("/api/fishing-zones/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const zone = await storage.getFishingZoneById(id);

      if (!zone) {
        return res.status(404).json({ error: "Fishing zone not found" });
      }

      res.json(zone);
    } catch (error) {
      console.error('Fishing zone error:', error);
      res.status(500).json({ error: "Failed to fetch fishing zone" });
    }
  });

  // Catch logs endpoints
  app.get("/api/catch-logs", async (req, res) => {
    try {
      const { userId } = req.query;
      const logs = await storage.getCatchLogs(userId as string);
      res.json(logs);
    } catch (error) {
      console.error('Catch logs error:', error);
      res.status(500).json({ error: "Failed to fetch catch logs" });
    }
  });

  app.post("/api/catch-logs", async (req, res) => {
    try {
      const validatedData = insertCatchLogSchema.parse(req.body);
      const log = await storage.createCatchLog(validatedData);
      res.status(201).json(log);
    } catch (error) {
      console.error('Create catch log error:', error);
      res.status(400).json({ error: "Failed to create catch log" });
    }
  });

  app.delete("/api/catch-logs/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const deleted = await storage.deleteCatchLog(id);

      if (!deleted) {
        return res.status(404).json({ error: "Catch log not found" });
      }

      res.json({ success: true, message: "Catch log deleted successfully" });
    } catch (error) {
      console.error('Delete catch log error:', error);
      res.status(500).json({ error: "Failed to delete catch log" });
    }
  });

  // Chat endpoints
  app.get("/api/chat/:userId", async (req, res) => {
    try {
      const { userId } = req.params;
      const messages = await storage.getChatMessages(userId);
      res.json(messages);
    } catch (error) {
      console.error('Chat messages error:', error);
      res.status(500).json({ error: "Failed to fetch chat messages" });
    }
  });

  app.post("/api/chat", async (req, res) => {
    try {
      const { message, userId, context } = req.body;

      if (!message || !userId) {
        return res.status(400).json({ error: "Message and userId are required" });
      }

      // Enhance context with fishing zones and location data for route optimization
      const enhancedContext = { ...context };

      // Add fishing zones to context
      try {
        const fishingZones = await storage.getFishingZones();
        enhancedContext.fishingZones = fishingZones;
      } catch (error) {
        console.error('Failed to fetch fishing zones for chat context:', error);
      }

      // Extract current location from context if available
      if (context?.weather?.latitude && context?.weather?.longitude) {
        enhancedContext.currentLocation = {
          latitude: context.weather.latitude,
          longitude: context.weather.longitude
        };
      }

      // Add weather service to context for location-based weather requests
      enhancedContext.weatherService = weatherService;

      // Get AI response with enhanced context
      const aiResponse = await geminiService.getFishingAdvice(message, enhancedContext);

      // Save chat message
      const chatMessage = await storage.createChatMessage({
        userId,
        message,
        response: aiResponse
      });

      res.json({
        id: chatMessage.id,
        message: chatMessage.message,
        response: chatMessage.response,
        timestamp: chatMessage.timestamp
      });
    } catch (error) {
      console.error('Chat error:', error);
      res.status(500).json({ error: "Failed to process chat message" });
    }
  });

  // Fishing conditions analysis endpoint
  app.post("/api/fishing-analysis", async (req, res) => {
    try {
      const { lat, lon, location } = req.body;

      if (!lat || !lon) {
        return res.status(400).json({ error: "Latitude and longitude are required" });
      }

      // Get current weather
      const weatherData = await weatherService.getCurrentWeather(lat, lon);

      // Get AI analysis
      const analysis = await geminiService.analyzeFishingConditions(
        weatherData, 
        location || weatherData.location
      );

      res.json({
        weather: weatherData,
        analysis,
        timestamp: new Date().toISOString()
      });
    } catch (error) {
      console.error('Fishing analysis error:', error);
      res.status(500).json({ error: "Failed to analyze fishing conditions" });
    }
  });

  // Translation endpoint
  app.post("/api/translate", async (req, res) => {
    try {
      const { message, language } = req.body;

      if (!message || !language) {
        return res.status(400).json({ error: "Message and language are required" });
      }

      const translatedMessage = await geminiService.translateMessage(message, language);
      res.json({ translatedMessage });
    } catch (error) {
      console.error('Translation error:', error);
      res.status(500).json({ error: "Failed to translate message" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser, 
  type WeatherData, 
  type InsertWeatherData,
  type FishingZone,
  type InsertFishingZone,
  type CatchLog,
  type InsertCatchLog,
  type ChatMessage,
  type InsertChatMessage
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Weather operations
  getWeatherData(location: string): Promise<WeatherData | undefined>;
  saveWeatherData(weather: InsertWeatherData): Promise<WeatherData>;
  getRecentWeatherData(limit?: number): Promise<WeatherData[]>;

  // Fishing zones
  getFishingZones(): Promise<FishingZone[]>;
  getFishingZoneById(id: string): Promise<FishingZone | undefined>;
  createFishingZone(zone: InsertFishingZone): Promise<FishingZone>;

  // Catch logs
  getCatchLogs(userId?: string): Promise<CatchLog[]>;
  getCatchLogById(id: string): Promise<CatchLog | undefined>;
  createCatchLog(log: InsertCatchLog): Promise<CatchLog>;
  deleteCatchLog(id: string): Promise<boolean>;

  // Chat messages
  getChatMessages(userId: string): Promise<ChatMessage[]>;
  createChatMessage(message: InsertChatMessage): Promise<ChatMessage>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private weatherData: Map<string, WeatherData>;
  private fishingZones: Map<string, FishingZone>;
  private catchLogs: Map<string, CatchLog>;
  private chatMessages: Map<string, ChatMessage>;

  constructor() {
    this.users = new Map();
    this.weatherData = new Map();
    this.fishingZones = new Map();
    this.catchLogs = new Map();
    this.chatMessages = new Map();

    // Initialize with some default fishing zones
    this.initializeDefaultData();
  }

  private async initializeDefaultData() {
    const defaultZones: InsertFishingZone[] = [
      {
        name: "Chennai Coast North",
        latitude: 13.0827,
        longitude: 80.2707,
        fishTypes: ["Mackerel", "Sardines", "Pomfret"],
        optimalConditions: { windSpeed: "5-15 km/h", tideLevel: "medium" },
        safetyRating: 8
      },
      {
        name: "Marina Beach Zone",
        latitude: 13.0524,
        longitude: 80.2824,
        fishTypes: ["Kingfish", "Tuna", "Barracuda"],
        optimalConditions: { windSpeed: "10-20 km/h", tideLevel: "high" },
        safetyRating: 9
      },
      {
        name: "Pulicat Lake",
        latitude: 13.4167,
        longitude: 80.3167,
        fishTypes: ["Crab", "Prawns", "Mullet"],
        optimalConditions: { windSpeed: "5-12 km/h", tideLevel: "low" },
        safetyRating: 7
      }
    ];

    // Sample catch log entries for demonstration
    const sampleCatchLogs: InsertCatchLog[] = [
      {
        fishType: "Mackerel",
        quantity: 15,
        weight: 3.2,
        location: "Chennai Coast North",
        latitude: 13.0827,
        longitude: 80.2707,
        price: 480,
        notes: "Good morning catch, favorable tide conditions",
        userId: "demo-user",
        weatherConditions: {
          temperature: 28,
          windSpeed: 12,
          conditions: "partly cloudy",
          tideLevel: "medium"
        }
      },
      {
        fishType: "Kingfish",
        quantity: 2,
        weight: 4.8,
        location: "Marina Beach Zone",
        latitude: 13.0524,
        longitude: 80.2824,
        price: 960,
        notes: "Caught during high tide, excellent quality fish",
        userId: "demo-user",
        weatherConditions: {
          temperature: 30,
          windSpeed: 18,
          conditions: "clear",
          tideLevel: "high"
        }
      },
      {
        fishType: "Pomfret",
        quantity: 8,
        weight: 2.1,
        location: "Chennai Coast North",
        latitude: 13.0827,
        longitude: 80.2707,
        price: 630,
        notes: "Evening catch, calm waters",
        userId: "demo-user",
        weatherConditions: {
          temperature: 26,
          windSpeed: 8,
          conditions: "clear",
          tideLevel: "low"
        }
      },
      {
        fishType: "Sardines",
        quantity: 25,
        weight: 1.5,
        location: "Pulicat Lake",
        latitude: 13.4167,
        longitude: 80.3167,
        price: 150,
        notes: "Large school spotted, quick catch session",
        userId: "demo-user",
        weatherConditions: {
          temperature: 27,
          windSpeed: 10,
          conditions: "partly cloudy",
          tideLevel: "medium"
        }
      }
    ];

    for (const zone of defaultZones) {
      await this.createFishingZone(zone);
    }

    // Add sample catch logs with different timestamps to show variety
    for (let i = 0; i < sampleCatchLogs.length; i++) {
      const log = sampleCatchLogs[i];
      // Create logs with timestamps from the past few days
      const daysAgo = i + 1;
      const sampleTimestamp = new Date();
      sampleTimestamp.setDate(sampleTimestamp.getDate() - daysAgo);
      sampleTimestamp.setHours(6 + (i * 3), 30, 0, 0); // Vary the times

      const catchLog: CatchLog = {
        ...log,
        id: randomUUID(),
        userId: log.userId || null,
        latitude: log.latitude || null,
        longitude: log.longitude || null,
        price: log.price || null,
        notes: log.notes || null,
        weatherConditions: log.weatherConditions || {},
        timestamp: sampleTimestamp
      };

      this.catchLogs.set(catchLog.id, catchLog);
    }
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { 
      ...insertUser, 
      id,
      language: insertUser.language || "en",
      preferences: insertUser.preferences || {},
      createdAt: new Date()
    };
    this.users.set(id, user);
    return user;
  }

  async getWeatherData(location: string): Promise<WeatherData | undefined> {
    return Array.from(this.weatherData.values()).find(
      (weather) => weather.location === location
    );
  }

  async saveWeatherData(insertWeather: InsertWeatherData): Promise<WeatherData> {
    const id = randomUUID();
    const weather: WeatherData = {
      ...insertWeather,
      id,
      tideLevel: insertWeather.tideLevel || null,
      timestamp: new Date()
    };
    this.weatherData.set(id, weather);
    return weather;
  }

  async getRecentWeatherData(limit = 10): Promise<WeatherData[]> {
    return Array.from(this.weatherData.values())
      .sort((a, b) => (b.timestamp?.getTime() || 0) - (a.timestamp?.getTime() || 0))
      .slice(0, limit);
  }

  async getFishingZones(): Promise<FishingZone[]> {
    return Array.from(this.fishingZones.values());
  }

  async getFishingZoneById(id: string): Promise<FishingZone | undefined> {
    return this.fishingZones.get(id);
  }

  async createFishingZone(insertZone: InsertFishingZone): Promise<FishingZone> {
    const id = randomUUID();
    const zone: FishingZone = {
      ...insertZone,
      id,
      fishTypes: insertZone.fishTypes || [],
      optimalConditions: insertZone.optimalConditions || {},
      createdAt: new Date()
    };
    this.fishingZones.set(id, zone);
    return zone;
  }

  async getCatchLogs(userId?: string): Promise<CatchLog[]> {
    const logs = Array.from(this.catchLogs.values());
    if (userId) {
      return logs.filter(log => log.userId === userId);
    }
    return logs.sort((a, b) => (b.timestamp?.getTime() || 0) - (a.timestamp?.getTime() || 0));
  }

  async getCatchLogById(id: string): Promise<CatchLog | undefined> {
    return this.catchLogs.get(id);
  }

  async createCatchLog(insertLog: InsertCatchLog): Promise<CatchLog> {
    const id = randomUUID();
    const log: CatchLog = {
      ...insertLog,
      id,
      userId: insertLog.userId || null,
      latitude: insertLog.latitude || null,
      longitude: insertLog.longitude || null,
      price: insertLog.price || null,
      notes: insertLog.notes || null,
      weatherConditions: insertLog.weatherConditions || {},
      timestamp: new Date()
    };
    this.catchLogs.set(id, log);
    return log;
  }

  async getChatMessages(userId: string): Promise<ChatMessage[]> {
    return Array.from(this.chatMessages.values())
      .filter(msg => msg.userId === userId)
      .sort((a, b) => (a.timestamp?.getTime() || 0) - (b.timestamp?.getTime() || 0));
  }

  async deleteCatchLog(id: string): Promise<boolean> {
    const exists = this.catchLogs.has(id);
    if (exists) {
      this.catchLogs.delete(id);
      return true;
    }
    return false;
  }

  async createChatMessage(insertMessage: InsertChatMessage): Promise<ChatMessage> {
    const id = randomUUID();
    const message: ChatMessage = {
      ...insertMessage,
      id,
      userId: insertMessage.userId || null,
      timestamp: new Date()
    };
    this.chatMessages.set(id, message);
    return message;
  }
}

export const storage = new MemStorage();

