  --sidebar-border: hsl(215, 28%, 17%);
  --sidebar-ring: hsl(217, 91%, 60%);
}

@layer base {
  * {
    @apply border-border;
  }

  html {
    height: 100%;
    scroll-behavior: smooth;
    overflow-x: hidden;
    -webkit-text-size-adjust: 100%;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    height: 100%;
    overflow-x: hidden;
    overflow-y: auto;
    -webkit-overflow-scrolling: touch;
    touch-action: manipulation;
    overscroll-behavior: none;
    position: relative;
  }

  #root {
    min-height: 100vh;
    overflow-x: hidden;
  }

  /* Fix mobile scrolling issues */
  * {
    -webkit-overflow-scrolling: touch;
    touch-action: manipulation;
  }

  /* Ensure proper scrolling for main content areas */
  main {
    overflow-x: hidden;
    padding-bottom: 80px; /* Account for bottom nav */
    touch-action: pan-y;
    position: relative;
    width: 100%;
  }

  /* Fix viewport for mobile browsers */
  .viewport-fix {
    min-height: 100vh;
    min-height: -webkit-fill-available;
  }
}

/* Custom wave animation for weather cards */
.wave-animation {
  background: linear-gradient(45deg, hsl(var(--secondary)), hsl(var(--primary)), hsl(var(--secondary)));
  background-size: 400% 400%;
  animation: wave 3s ease-in-out infinite;
}

@keyframes wave {
  0%, 100% { 
    background-position: 0% 50%; 
  }
  50% { 
    background-position: 100% 50%; 
  }
}

/* Fishing condition specific gradients */
.weather-safe { 
  background: linear-gradient(135deg, hsl(159, 78%, 42%), hsl(147, 78%, 42%)); 
}

.weather-caution { 
  background: linear-gradient(135deg, hsl(42, 92%, 56%), hsl(45, 93%, 47%)); 
}

.weather-danger { 
  background: linear-gradient(135deg, hsl(0, 72%, 51%), hsl(356, 91%, 54%)); 
}

/* Touch-friendly button sizes */
.touch-target {
  min-height: 48px;
  min-width: 48px;
}

/* Floating elements */
.floating-action {
  position: fixed;
  bottom: 24px;
  right: 24px;
  z-index: 50;
}

/* Chat sidebar transitions */
.sidebar-hidden {
  transform: translateX(100%);
}

.sidebar-visible {
  transform: translateX(0);
}

/* Custom scrollbar for chat */
.chat-scroll::-webkit-scrollbar {
  width: 4px;
}

.chat-scroll::-webkit-scrollbar-track {
  background: hsl(var(--muted));
}

.chat-scroll::-webkit-scrollbar-thumb {
  background: hsl(var(--muted-foreground));
  border-radius: 2px;
}

/* Enhanced mobile scroll performance */
@supports (-webkit-touch-callout: none) {
  /* iOS specific optimizations */
  .ios-scroll {
    -webkit-overflow-scrolling: touch;
    overflow-scrolling: touch;
  }

  /* Fix for iOS momentum scrolling */
  body {
    -webkit-overflow-scrolling: touch;
  }
}

/* Loading pulse animation for weather data */
.weather-loading {
  background: linear-gradient(90deg, hsl(var(--muted)) 25%, hsl(var(--background)) 50%, hsl(var(--muted)) 75%);
  background-size: 200% 100%;
  animation: loading-pulse 1.5s infinite;
}

@keyframes loading-pulse {
  0% {
    background-position: 200% 0;
  }
  100% {
    background-position: -200% 0;
  }
}

/* Mobile optimizations */
@media (max-width: 768px) {
  html, body {
    height: 100%;
    overflow-x: hidden;
    -webkit-overflow-scrolling: touch;
  }

  body {
    -webkit-tap-highlight-color: transparent;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    user-select: none;
  }

  /* Allow text selection for inputs and content areas */
  input, textarea, [contenteditable], .prose {
    -webkit-user-select: text;
    user-select: text;
  }

  .touch-target {
    min-height: 44px;
    min-width: 44px;
  }

  /* Larger tap targets on mobile */
  button, input, select, textarea {
    font-size: 16px; /* Prevents zoom on iOS */
  }

  /* Fix scrolling containers */
  .scroll-container {
    -webkit-overflow-scrolling: touch;
    overflow-scrolling: touch;
  }

  /* Ensure proper spacing for fixed bottom nav */
  main {
    padding-bottom: 5rem !important;
    margin-bottom: 0;
    min-height: 100vh;
  }

  /* Fix iOS safari scrolling bounce */
  .ios-scroll {
    -webkit-overflow-scrolling: touch;
    overflow-scrolling: touch;
    overscroll-behavior: contain;
  }

  /* Prevent horizontal scrolling on all containers */
  .page-container {
    overflow-x: hidden !important;
    width: 100% !important;
    max-width: 100vw !important;
  }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  :root {
    --border: hsl(0, 0%, 0%);
    --foreground: hsl(0, 0%, 0%);
  }

  .dark {
    --border: hsl(0, 0%, 100%);
    --foreground: hsl(0, 0%, 100%);
  }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  .wave-animation,
  .weather-loading {
    animation: none;
  }

  * {
    transition-duration: 0.01ms !important;
    animation-duration: 0.01ms !important;
  }
}

/* Print styles */
@media print {
  .floating-action,
  .sidebar-visible,
  nav {
    display: none !important;
  }
}

